	};
	if( getSubString( this\Contenido, 1,3 )  = "740" or  getSubString( this\Contenido, 1,3 )  = "750")
	then 
	{
		%totalPJ3:=%totalPJ3+convertToI(getSubString(this\Contenido, 53+ countString(this\Contenido, "ã") + countString(this\Contenido, "Ã"),14)) +convertToI(getSubString(this\Contenido, 67+ countString(this\Contenido, "ã") + countString(this\Contenido, "Ã"),14))+convertToI(getSubString(this\Contenido, 81+ countString(this\Contenido, "ã") + countString(this\Contenido, "Ã"),14))+convertToI(getSubString(this\Contenido, 95+ countString(this\Contenido, "ã") + countString(this\Contenido, "Ã"),14))+convertToI(getSubString(this\Contenido, 109+ countString(this\Contenido, "ã") + countString(this\Contenido, "Ã"),14))+convertToI(getSubString(this\Contenido, 123+ countString(this\Contenido, "ã") + countString(this\Contenido, "Ã"),14))
